<?php class SU_Ui {	// add media files like css or js	function add_external($url,$type) {		global $externalResource;				if (file_exists(BASE_DIR . $url)) {			$url .= '?' . filemtime(BASE_DIR . $url);		}				switch ($type) {			case 'css':				$externalResource[$type][] = '<link rel="stylesheet" type="text/css" href="'.$url.'" />';				break;			case 'js':				$externalResource[$type][] = '<script src="'.$url.'"></script>';		}	}	function load_external($type='all') {		global $externalResource;		if (!is_array($externalResource)) return;		$htmlExternal = '';		if ($type=='all') {			if (array_key_exists('css',$externalResource)) {				// css first				$htmlExternal .= "\n\t<!-- Assets - CSS -->\n\t";				$htmlExternal .= implode("\n\t",$externalResource['css']);				$htmlExternal .= "\n\t";			}			if (array_key_exists('js',$externalResource)) {				// js second				$htmlExternal .= "\n\t<!-- Assets - Javascript -->\n\t";				$htmlExternal .= implode("\n\t",$externalResource['js']);				$htmlExternal .= "\n\t";			}			// remove from array			unset($externalResource['js']);			unset($externalResource['css']);						// others			foreach ($externalResource AS $key => $value) {				$htmlExternal .= "\n\t<!-- Assets - ".$key." -->\n\t";				$htmlExternal .= implode("\n\t",$externalResource[$key]);				$htmlExternal .= "\n\t";			}		} else {			// user specifik type			$htmlExternal .= "\n\t<!-- Assets - ".$type." -->\n\t";			$htmlExternal .= implode("\n\t",$externalResource[$type]);			$htmlExternal .= "\n\t";		}		echo $htmlExternal;	}	/**	 * Get either a Gravatar URL or complete image tag for a specified email address.	 *	 * @param string $email The email address	 * @param string $s Size in pixels, defaults to 80px [ 1 - 512 ]	 * @param string $d Default imageset to use [ 404 | mm | identicon | monsterid | wavatar ]	 * @param string $r Maximum rating (inclusive) [ g | pg | r | x ]	 * @param boole $img True to return a complete IMG tag False for just the URL	 * @param array $atts Optional, additional key/value attributes to include in the IMG tag	 * @return String containing either just a URL or a complete image tag	 * @source http://gravatar.com/site/implement/images/php/	 */	function gravatar( $email, $s = 80, $d = 'identicon', $r = 'pg', $img = false, $atts = array() ) {		$url = 'http://www.gravatar.com/avatar/';		$url .= md5( strtolower( trim( $email ) ) );		$url .= "?s=$s&d=$d&r=$r";		if ( $img ) {			$url = '<img src="' . $url . '"';			foreach ( $atts as $key => $val )				$url .= ' ' . $key . '="' . $val . '"';			$url .= ' />';		}		return $url;	}	function number($number) {		return number_format($number, 0, ',', ' ');	}		function yesno($string) {		return ($string) ? 'Yes' : 'No';	}}?>